class Solution {
public:
    int minDeletions(string s) {
        unordered_map<char,int>ump;
        for(int i =0; i< s.size(); i++){
            ump[s[i]]++;
        }
        priority_queue<int>pq;
        for(auto it : ump){
            pq.push(it.second);

        }
        int count = 0;
        while(pq.size() >1) {
            int first = pq.top(); 
            pq.pop();
            int second = pq.top();
            pq.pop();
            if(first == second && second > 0){
                second--;
                count++;
                pq.push(second);
                pq.push(first);
            }
            else{
                pq.push(second);
            }
        }
        return count;

    }
};
