class Solution {
public:
    int minDeletions(string s) {
        unordered_map<char,int>mp;
        vector<int>v;

        for(auto ch : s)
        {
            mp[ch]++;
        }

        for(auto it=mp.begin();it!=mp.end();it++)
        {
           v.push_back(it->second);	
        }

        sort(v.begin(),v.end(),greater<int>());

        int count=0;

        for(int i=1;i<v.size();i++)
        {
            if(v[i-1]==0)
            {
                count=count+(abs(v[i-1]-v[i]));
                v[i]=0;
            }
            else if(v[i-1]<=v[i])
            {
                count=count+(abs(v[i-1]-v[i])+1);
                v[i]=v[i-1]-1;
            }
        }
        return count;
    }
};
