class Solution {
public:
    int minDeletions(string s) {
        vector <int>  freq(26, 0); 
    for (char c : s) 
    {
        freq[c - 'a']++;
    }
      sort(freq.begin(), freq.end(), greater<int>());  
    int count = 0;
    int maxfreq = freq[0];
    for (int i = 0; i < 26 && freq[i] > 0; i++) 
    {
        if (freq[i] > maxfreq)
        {
            count += freq[i] - maxfreq;
            freq[i] = maxfreq;
        }
        maxfreq = max(0, freq[i] - 1);
    }
      return count;
    }
};
```

