class Solution {
public:
    int minDeletions(string s) {
        vector<int> freq(26);
        unordered_set<int> set;
        int ans = 0;

        for(char ch: s)
            freq[ch - 'a']++;

        for(char ch = 0; ch < 26; ch++)
            while(freq[ch] and !set.insert(freq[ch]).second)
                freq[ch]--, ans++;

        return ans;
    }
};
