class Solution {
public:
    int minDeletions(string s) {
        vector<int> freq(26, 0);
        for(char& c : s)
            freq[c-'a']++;

        unordered_set<int> seen;
        int res = 0;

        for(int i = 0; i < 26; i++) {   // go through each freq
            // if this freq is non-zero and already used, then decrease it until a unique freq is found
            // increase the delete count by 1 for each decrement in freq
            while(freq[i] > 0 and seen.find(freq[i]) != seen.end()) {
                freq[i]--;
                res++;
            }
            seen.insert(freq[i]);
        }
        return res;
    }
};
