class Solution {
public:
    int minDeletions(string s) {
        // this is a variation of hash table 
        // here we will calculate the frequecy then we will sort the frequencies in descending order 
        // the we will run the loop and find the same frequencies and reduce it by 1 for each similarity 
        // at last we will stop
        // edge case can be when string frequecy of the eleement is same for all the charcter and string length is greater than the number of charcters in the string

        int n = s.size();

        // making frequency array 
        vector<int> hash ( 26 , 0);

        for ( char ch : s ) hash[ch - 'a']++;

        // now sorting the hash function 
        sort( hash.rbegin() , hash.rend() );

        int thresholdFrq = hash.at(0);
        int ans = 0 ;

        for ( auto frequency : hash ){
            if ( frequency > thresholdFrq){
                if ( thresholdFrq > 0 )
                    ans += frequency - thresholdFrq ;
                else 
                    ans += frequency ;
            }

            thresholdFrq = min ( thresholdFrq - 1 , frequency - 1);
        }

        return ans;
    }
};
```

