```
class Solution {
    public int minDeletions(String s) {
        int[] cnt = new int[26];
        Set<Integer> used = new HashSet<>();
        // extract the frequency
        for(char ch: s.toCharArray()) {
            cnt[ch - 'a']++;
        }
        // iterate over the cnt array and check freq 
        int minDeletions = 0;
        for(int i = 0; i < 26; i++) { 
            int freq = cnt[i];
            while(freq > 0) {        
                if(!used.contains(freq)) { // if freq is not present in set(freq not encountered earlier), then add it
                    used.add(freq);
                    break;
                }
                // already present then reduce the freq and increase the minDeletions
                freq--;
                minDeletions++;
            }
        }
        return minDeletions;
    }
}
