class Solution {
    fun numFactoredBinaryTrees(A: IntArray, MOD: Long = (1e9 + 7).toLong()): Int {
        var N = A.size
        var seen = A.toSet()
        fun go(x: Int): Long {
            var cnt = 1L
            for (y in A) {
                if (x % y != 0)
                    continue
                var z = x / y
                if (seen.contains(z))
                    cnt = (cnt + (go(y) * go(z) % MOD)) % MOD
            }
            return cnt
        }
        var t = 0L
        for (x in A)
            t = (t + go(x)) % MOD
        return t.toInt()
    }
}
```

