class Solution {
public:
    using LL = long long;
    using VI = vector<int>;
    using Set = unordered_set<int>;
    using fun = function<LL(int)>;
    using Map = unordered_map<int, LL>;
    int numFactoredBinaryTrees(VI& A, LL MOD = 1e9 + 7, Map m = {}, LL t = 0) {
        Set seen{ A.begin(), A.end() };
        sort(A.begin(), A.end());
        for (auto x: A)
            m[x] = 1LL;
        for (auto x: A) {
            for (auto y: A) {
                if (x % y)
                    continue;
                auto z = x / y;
                if (seen.find(z) != seen.end())
                    m[x] = (m[x] + (m[y] * m[z] % MOD) % MOD);
            }
        }
        for (auto [_, cnt]: m)
            t = (t + cnt) % MOD;
        return t;
    }
};
