class Solution:
    def numFactoredBinaryTrees(self, arr: List[int]) -> int:
        mod = 10 ** 9 + 7
        m = {}
        res = 0

        arr.sort()

        for head in arr:
            m[head] = 1
            for child1 in m.keys():
                child2 = head / child1
                if child2 in m:
                    tmp = (m[child1] * m[child2]) % mod
                    m[head] = (m[head] + tmp) % mod

            res = (res + m[head]) % mod

        return res
