        for i in range(1, N):
            dp[A[i]] = 1
            for j in range(i):
                div, mod = divmod(A[i], A[j])
                if div < A[j]: break
                if mod != 0: continue
                if div in dp: 
                    mul = dp[div] * dp[A[j]]
                    dp[A[i]] += mul if div == A[j] else 2 * mul

        return sum(dp.values()) % (10 ** 9 + 7)
