class Solution {
public:
	//given in problem
	const int mod = 1e9 + 7;
	int numFactoredBinaryTrees(vector<int> &arr)
	{
		//define n as zize of array
		int n = arr.size();
		vector < long long > dp(n, 1);
		unordered_map<int, int> mp;
		sort(arr.begin(), arr.end());
		//iterate the loop
		for (int i = 0; i < n; i++)
		{
			//store the index of various elements of the array
			mp[arr[i]] = i;
			for (int j = i - 1; j >= 0; j--)
			{
				//to check whether arr[j] is a factor of arr[i] or not
				if (arr[i] % arr[j] == 0)
				{
					//store to temp
					int temp = arr[i] / arr[j];
					//now check if count of temp in map exists then
					if (mp.count(temp))
					{
						//check the presence of the required no in map,
						//If it is in the map then increase dp[i] by the product of the dp[j]
						//dp[mp[temp]] taking the modulus
						dp[i] += (dp[j] * dp[mp[temp]]) % mod;

					}
				}
			}

		}
		long long ans = 0;
		ans = accumulate(dp.begin(), dp.end(), 0LL);
		//return total sum with mod
		return (ans % mod);
	}
};

