class Solution {
    public int numFactoredBinaryTrees(int[] arr) {
        Arrays.sort(arr);
        int n = arr.length ,mod = 1_000_000_007;
        long res=0, ways[] = new long[n];

        for(int i=0,l=0,r=0 ;i<n; i++){
            ways[i] = 1;
            //Two-poitner Approach
            l = 0; r = i-1;
            while(l <= r){
			/*Instead of checking arr[l]*arr[r] == arr[i], to prevent overflow
			use arr[l] == arr[i]/arr[r]. */
                if( arr[l]*1.0 == arr[i]*1.0/arr[r]){         
                    ways[i] += ways[l]*ways[r]*( l==r ? 1 : 2 );
                    l++;r--;
                }
                else if ( arr[l]*1.0 > arr[i]*1.0/arr[r]) r--;
                else l++;
            }
            res = (res+ways[i]) % mod;
        }

        return (int)res;
    }
}

