//Approach 1 : Top-Down Recursive Approach 
/*
1 . First sort the array (Now we can say 2 number multiple is always its left side )
2 . Using 2 Pointer find out the 2 numbers (s=0 && e =n-1)
3 . if arr[s]*arr[e]==arr[curInd] It means arr[s] and arr[e] is 2 child (and increase s++ and decrese e--)
{
If we found 2 no then again recursively call for child (total no of tree formed using both child)
child1 = x
child2 = y
total no of tree for current parent = x*y (if s==e)
or
total no of tree for current parent = 2 * x*y (if s!=e)
}
	if arr[s]*arr[e]>arr[curInd] then we have to decrease our e--
	else s++
4 .Using this approach we can find all no of pairs for every index 
*/
/*
Example : 2 10 4 5
1 . sort the array :-> 2 4 5 10
2 . start from index 0 or n-1( here i am starting with n-1) 
s = 0 
e = 3-1 = 2
2 4 5 10
^   ^
mul = 2*5 = 10
Here we got 10 (2,5 is one pair)
2!=5 (or s!=e)
dp[curInd] = 2 * dp[s]*dp[e]
again call for n-1 recursively
*/
class Solution {
public:
    vector<int> dp;
    int mod = 1000000007;
    long solve(vector<int>&arr,int ind){
        if(ind==-1) return 0; 
        if(dp[ind]!=-1) return dp[ind];
        long cnt = 1;
        int s = 0;
        int e = ind-1;
        while(s<=e){
            long mul = (long)arr[s]*arr[e];
            if(mul==arr[ind]){
                long temp = (solve(arr,s) * solve(arr,e))%mod;
                if(s!=e) temp=temp*2;
                cnt = (cnt+temp)%mod;
                s++;
                e--;
            }
            else if(mul>arr[ind]) e--;
            else s++;
        }
        return dp[ind] = cnt;
    }
    int numFactoredBinaryTrees(vector<int>& arr) {
       sort(arr.begin(),arr.end());
      dp.resize(arr.size(),-1);
        long cnt = 0;
        for(int i=0;i<arr.size();i++){
            cnt=(cnt+solve(arr,i))%mod;
        }
        return cnt;
    }
};
