```C#
public class Solution {
    private static int _mod = 1000000007;
    public int NumFactoredBinaryTrees(int[] arr) {
        Dictionary<int, long> memo = new ();

        HashSet<int> nums = new (arr);
        int ans = 0;
        for (int i = 0; i < arr.Length; i++)
            ans = (ans + (int)Solve(arr[i])) % _mod;

        return ans;

        long Solve(int num) {
            if (memo.TryGetValue(num, out var memoizedResult)) return memoizedResult;

            long count = 0;
            if (nums.Contains(num)) {
                count++;

                int sqrt = (int)Math.Sqrt(num);
                for (int i = 2; i <= sqrt; i++) {
                    if (num % i == 0) {
                        int t = num / i;
                        long p = Solve(i) * Solve(t) % _mod;
                        count = (count + (int)p) % _mod;
                        if (i != t) count = (count + (int)p) % _mod;
                    }
                }
            }

            memo[num] = count;
            return count;
        }
    }
}
