int numFactoredBinaryTrees(vector<int>& arr) {
    std::sort(arr.begin(), arr.end());
    int n = arr.size();
    vector<long long> dp(n, 1);
    int mod = 1e9 + 7;
    std::unordered_map<int, int> pos;
    for(int i = 0; i < n; ++i) {
        pos[arr[i]] = i;
    }

    for(int i = 0; i < n; ++i) {
        for(int j = 0; j < i; ++j) {
            if (arr[i] % arr[j] > 0 ) continue;
            int current = arr[i] / arr[j];
            if (pos.find(current) != pos.end()) {
                dp[i] += dp[pos[current]] * dp[j];
            }
        }
    }

    long long res = 0;
    for(auto& c : dp) {
        res += c;
    }
    return res % mod;
}

