class Solution {
public:
    int numFactoredBinaryTrees(vector<int>& arr) {
        int n = arr.size(), mod = 1e9+7;
        sort(arr.begin(),arr.end());
        map<int,int>idx;
        map<int,long long>mp;
        for(int i=0; i<n; i++){
            idx[arr[i]] = i;
            mp[i] = 1;
        }
        for(int i=1; i<n; i++){
            for(int j=0; j<i; j++){
                if(arr[i] % arr[j] == 0 && idx.find(arr[i]/arr[j]) != idx.end()){
                    mp[i] += (mp[j] * mp[idx[arr[i]/arr[j]]]);
                    mp[i] = mp[i]%mod;
                }
            }
        }

        long long ans = 0;
        for(auto i : mp){
            ans += i.second;
            ans %= mod;
        }
        return ans%mod;
    }
};
