class Solution {
public:
    #define ll long long
    ll mod = 1e9+7;
    int numFactoredBinaryTrees(vector<int>& arr) {
        int n = arr.size();
        sort(arr.begin(),arr.end());
        vector<ll> dp(n);
        dp[0] = 1;
        ll ans = 1;
        for(int idx = 1;idx<n;idx++)
        {
            ll target = arr[idx];
            int i=0,j=idx-1;
            ll ways = 1;
            while(i<=j)
            {
                ll mul = (((long)arr[i])*(arr[j]));
                if(mul == target)
                {
                    if(i == j)
                    {
                        ways += (dp[i]*dp[j])%mod;
                    }
                    else
                    {
                        ways += (((long)dp[i]*(long)dp[j])*2)%mod;
                    }
                    i++,j--;
                }
                else if(mul > target)
                {
                    j--;
                }
                else if(mul < target)
                {
                    i++;
                }
            }
            dp[idx] = ways;
            ans = (long)(ans + dp[idx])%mod;
        }
        return ans;
    }
};
```

