class Solution:
    def numFactoredBinaryTrees(self, array: List[int]) -> int:

        array.sort() 

        dct = dict(zip(array,range(1,len(array) + 1)))

        lst = [1] * len(array)

        exp = 10**9 + 7

        for i in range(len(array)):
            for j in range(i):
                if array[i] % array[j] == 0 and dct.get(array[i] // array[j],0):
                        lst[i] = lst[i] + lst[j] * lst[dct[array[i] // array[j]]-1]
        return sum(lst) % exp
