typedef long T;

class Solution {
public:
    int mod = 1e9 + 7;
    T modMul(T a,T b){
        a %= mod;
        b %= mod;
        a *=  b;
        a %= mod;
        return a;
    }
    T modAdd(T a,T b){
        a %= mod;
        b %= mod;
        a += b;
        a %= mod;
        return a;
    }
    int numFactoredBinaryTrees(vector<int>& arr) {
        sort(arr.begin(),arr.end());
        int n = arr.size();

        map<T,T> mp;
        for(auto &x : arr){
            mp[x] = 1;
        }
        int i,j;
        T sum = 1;
        for(i=1;i<n;i++){
            for(j=0;j<i;j++){
                if(arr[i]%arr[j]==0){
                    if(mp.find(arr[i]/arr[j]) != mp.end()){
                        // mp[ arr[i] ] =(mp[arr[i]]+mp[arr[j]] * mp[ arr[i]/arr[j] ])%mod;
                        mp[ arr[i] ] = modAdd( mp[arr[i]], modMul( mp[arr[j]], mp[arr[i] / arr[j] ] ) );

                    }
                }
            }
            // sum = (sum + mp[arr[i]])%mod;
            sum = modAdd( sum , mp[arr[i]] );
        }
        return sum;
    }
};
