class Solution {
public:
    int numFactoredBinaryTrees(vector<int>& arr) {
        unordered_map<int,long long int> mpp;
        sort(arr.begin(),arr.end());
        for(auto it: arr){
            mpp.insert({it,1}); // initilizing all the values in the map as 1
        }
        for(int i=1;i<arr.size();i++){
            long long int count=0;
            for(int j=0;j<i;j++){
                if(arr[i]%arr[j]==0){
                    if(mpp.find(arr[i]/arr[j])!=mpp.end()){
                        count+=mpp[arr[j]]*mpp[arr[i]/arr[j]]; //incrementing count
                    }
                }
            }
            mpp[arr[i]]+=count; //updating the new value in the map
        }
        long long int res=0;
        for(auto it: mpp){
            res=res+it.second;
        }
        return res%1000000007;
    }
};
