int MOD =  1e9 + 7;


class Solution
{
public:

	long long modMul(long long a, long long b){
		return ((a%MOD) * (b%MOD))%MOD;
	}

	unordered_map<int, vector<int>> mp;
	long long dp[20][1005];
	long long numFactoredBinaryTrees(vector<int> &arr)
	{
		memset(dp, -1, sizeof(dp));
		for(int i = 0; i<arr.size();i++){
			if(mp.count(arr[i]) > 0){
				mp[arr[i]].push_back(i);
			}else{
				mp[arr[i]] = {i};
			}
		}
		long long tmp = 0;
		for (int j = 0; j < arr.size(); j++)
		{
			tmp += makeTrees(arr, 0, j);
			tmp%=MOD;
		}

		return tmp;
	}

	long long makeTrees(vector<int> arr, int level, int parent)
	{

		if(dp[level][parent]!=-1)
			return dp[level][parent];
		long long cnt = 1;


		for(int i = 0;i<arr.size();i++){

			if(arr[parent]%arr[i] !=0)
				continue;

			int target = arr[parent]/arr[i];
			if(mp.count(target) == 0)
				continue;

			vector<int>tt = mp[target];

			for(int j=0;j<tt.size();j++){
				cnt+= (modMul((makeTrees(arr, level+1, i)),(makeTrees(arr, level+1, tt[j]))));
				cnt%=MOD;
			}
		}
		dp[level][parent] = cnt;
		return cnt;
	}
};
