class Solution:
    def numFactoredBinaryTrees(self, arr: List[int]) -> int:        
        arr.sort()
        arr_set = set(arr)

        @cache
        def dp(val):
            r = 1
            for i in range(len(arr)):
                left = arr[i]
                if left >= val:
                    break
                right = val / left
                if right in arr_set:
                    l_comb = dp(left)
                    r_comb = dp(right)

                    r += 1
                    r += l_comb-1
                    r += r_comb-1
                    r += (l_comb-1) * (r_comb-1)
            return r

        answer = 0
        for n in arr:
            answer += dp(n)

        return answer % 1000000007
