class Solution {
    private static int MODULO = 1000000007;

    public int numFactoredBinaryTrees(int[] arr) {
        int n = arr.length;
        Arrays.sort(arr);

        long[] dp = new long[n];

        // Map is used to check if any element is present which can be multiplied with the child to give the value at the parent node
        HashMap<Integer, Integer> map = new HashMap<Integer, Integer>();

        // It is possible to create 1 binary tree with each elements
        Arrays.fill(dp,1);

        // Add the elements along with their index in the map
        for(int index = 0 ; index < n ; index++) {
            int element = arr[index];
            map.put(element, index);
        }

        // It is not possible to create another binary tree 
        // So start from i = 0;
        for(int i = 1 ; i < n ; i++) {
            // Check with the elements less than the current element 
            for(int j = 0 ; j < i ; j++) {
                // arr[j] is the left child
                if(arr[i] % arr[j] == 0) {
                    // Element to be multiplied with the child to give the value at the parent node
                    int rightChild = arr[i] / arr[j]; 

                    // If map contains the element
                    if(map.containsKey(rightChild)) {
                        // No: of ways the product can be formed with the particular left and right child
                        int indexrightChild = map.get(rightChild);
                        long ways = (dp[j] * dp[indexrightChild]) % MODULO;
                        dp[i] = (dp[i] +  ways) % MODULO;
                    }
                }    
            }
        }

        long count = 0;
        for(long c : dp) {
            count += c;
        }

        int result = (int)(count % MODULO);

        return result;
    }
}
