class Solution {
    public int numFactoredBinaryTrees(int[] a) {
        long output = 0L , mod = (long)1e9+7;
        Arrays.sort(a);
        HashMap<Integer,Long> map = new HashMap<>();
        for(int i=0;i<a.length;i++)
