```
class Solution {
public:
    int numFactoredBinaryTrees(vector<int>& arr) {

        long long n = arr.size(), ans = 0, mod = 1e9+7;

        // sorting because we can check for smaller values as factor
        sort(arr.begin(), arr.end());

        // base case -> each node will have at least 1 tree, itself as root
        vector<long long> dp(n, 1);

        // to keep track of indices
        unordered_map<int, int> vis;
        for(int i = 0; i < n; i++) vis[arr[i]] = i;

        // counting for current node as parent
        for(int parent = 0; parent < n; ++parent) {
            // finding a potential left child 
            for(int left = 0; left < parent; ++left) {
                if(arr[parent] % arr[left] == 0) {
                    // if there exists a right such that arr[parent] = arr[left] * arr[right]
                    if(vis.find(arr[parent] / arr[left]) != vis.end()) {
                        int right = arr[parent] / arr[left];
                        // trees[parent] = trees[left] * trees[right]
                        dp[parent] += (dp[left] % mod * dp[vis[right]] % mod) % mod;
                    }
                }
            }
            // total trees by using current node as parent
            ans += dp[parent] % mod;
        }

        return ans % mod;
    }
};
```

