        int max = arr[arr.length-1];
        Map<Integer, Integer> map = new HashMap<>();
        for (int num : arr) map.put(num, 1);
        for (int i = 0; i < arr.length; i++) {
            long count = map.get(arr[i]);
            result += count;
            if (result > 1000000007) result %= 1000000007;            

            for (int j = 0; j < i; j++) {
                long temp = (long) arr[i]*arr[j];
                if (temp > max) break;
                int product = (int) temp;
                if (map.containsKey(product)) {
                    long newVal = 2*map.get(arr[j]);
                    if (newVal > 1000000007) newVal %= 1000000007;
                    newVal *= count;
                    if (newVal > 1000000007) newVal %= 1000000007;
                    newVal += map.get(product);
