class Solution {
public:
	unordered_set<ll> s;
	unordered_map<ll,ll> t;
	ll m = 1000000000+7;
	ll f(int a,vector<int>& arr){
		if(s.find(a)==s.end())return 0;
		if(t.find(a)!=t.end())return t[a];

		ll cnt=1;
		for(int i=0; i<arr.size(); i++){
			//cheak if arr[i] factors are present or not
			if(a%arr[i]==0 and s.find(a/arr[i])!=s.end()){
				// then recursively call their factor also
				cnt += (f(arr[i],arr)%m * f(a/arr[i],arr)%m)%m;
				// cnt = (cnt+k);
			}
		}
		return t[a] = cnt;
	}
	int numFactoredBinaryTrees(vector<int>& arr) {
		s.clear();
		int n = arr.size();
		ll res = 0;
		for(auto i: arr){
			s.insert(i);
		}
		for(int i=0; i<n; i++){
			//make it as root
			res += f(arr[i],arr);
		}
		return res%m;
	}
};

