                    if(num1==num2){
                        var1 = keyasroot[num1];
                        var2 = keyasroot[num2];
                        temp_prod = (var1*var2)%mod;
                        curr_count = (curr_count + temp_prod)%mod;
                    }
                    else{
                        var1 = keyasroot[num1];
                        var2 = keyasroot[num2];
                        temp_prod = (var1*var2)%mod;
                        temp_prod = (temp_prod*2)%mod;
                        curr_count = (curr_count + temp_prod)%mod;
                    }
                }
            }
            keyasroot[arr[i]] = curr_count;
            total_count = (total_count + curr_count)%mod;
        }
