    int kadane(vector<int>& arr, bool IsTwice) {
        long long M = 1000000007;
        int n = arr.size();
        int cSum = INT_MIN; // currentSum
        int mSum = INT_MIN; //maxSum

		// loop will run twice, if isTwice is true
		// basically we will be doing kadane for the array twice
        for(int j=0; j<(IsTwice?2:1); j++) {
            for(int i=0; i<n; i++) {
                if(cSum>=0) {
                    cSum += arr[i];
                } else {
                    cSum = arr[i];
                }
                if(cSum>mSum) {
                    mSum =  cSum;
                }
                cSum %= M;
                mSum %= M;
                // cout<<arr[i]<<" ";
            }   
        }
        return mSum;
    }

    int kConcatenationMaxSum(vector<int>& arr, int k) {
        long long M = 1000000007;
        if(k==1) return kadane(arr,false);
        long long sum = (accumulate(arr.begin(),arr.end(),0))%M;
        long long ans = 0;

        // SO basically there are only 2 cases. Either the sum>=0 || sum<0        
        if(sum>=0) {
            // Now when the sum is greater than zero, one appraoch that comes in that is to directly multiply the sum k number of times, but there is a catch.
            // What if there is a number is a array like: 
            // [4, -6, 3] and k=3
            // The overall sum is +ve
            // [4, -6, 3, 4, -6, 3, 4, -6, 3] -> Net Array (sum(array)*3)
            // [0,  1, 2, 3,  4, 5, 6,  7, 8] -> Indicecs

            // Here if we just add all the elements, sum*k format, 
            // then we unnecessarily add last -6 and 3 to the sum, which contributes negatively
            // Now if we take Kadane with k=2 on the same array, the algorithm will surely avoid considering the -6 and 3.

            // Basically every array, like [4, -6, 3] will have a postive part and a negative part
            // like the part 4, -6 -> constributes -2 negatively
            // and the part -6, 3 -> contributes -3 negatively.
            // So we will try to avoid -6, 3 part.

            // Now we have merged k arrays and we apply Kadane to last 2 arrays of the merged array.
            // Why last 2?
            // Becasuse we want to avoid the last 2 elements of individual array, as they contribute negatively.
              // If the net negative effect was provided by the prefix elements, then we would have taken first 2 arrays for kadane
            //But anyhow that doesnt affect our answer.

            //Now if we applly kadane on 
            // [4, -6, 3, 4, -6, 3, 4, -6, 3]
            // [0,  1, 2, 3,  4, 5, 6,  7, 8]

            // on last two arrays, i.e from the index 3 to 8, then the subarray it will choose for the maximum sum is
            // from index range [5,6] (inclusive)
            // Now merged array elements in the range [7,8] are unused, which we avoided.
            // merged Array elements in the range [3,4] is also unused as they did not contribute to maximum subarray when k=2.
            // But we can merge [3,4] ranges with index 2, and it forms a complete original individual array which contributes positively.
            // Now we can form k-2 such pairs, because we have untoched k-2 pairs and all nth array with form a pair with (n+1)th array.
            ans = ((kadane(arr,true)) + (((k-2)*sum)%M))%M;
        } else 
        {   
            // we dont give a damn for negative arrays as we know they contribute negative as a whole.
            // But everyone deserves a second chance in life 🙂,
            // So we will try merging two negative sum arrays, to see if it results in a postive array.
            // For example: [1,-10,2] // ans = 3
            ans = kadane(arr,true);
        }
        if(ans<0) return 0;
        return ans;
    }
};

