class Solution {

public:
    static int mod;

    int kConcatenationMaxSum(const vector<int>& arr, int k) {
        int n = arr.size();
        int64_t sum = accumulate(arr.begin(), arr.end(), 0);
        vector<int> buf;
        buf.reserve(n * 2);
        copy(arr.begin(), arr.end(), back_inserter(buf));
        copy(arr.begin(), arr.end(), back_inserter(buf));
        if (k == 1) return h(arr);
        if (k == 2) return h(buf);
        if (sum <= 0) return h(buf);
        return ((k - 2) * (sum % mod) + h(buf)) % mod;
    }

    int h(const vector<int>& arr) {
        int64_t res = 0, sofar = 0;
        for (int i : arr) {
            sofar = max((int64_t)0, sofar + i);
            res = max(res, sofar);
        }
        return res % mod;
    }
};

int Solution::mod = 1e9 + 7;
