class Solution {

	public int kConcatenationMaxSum(int[] arr, int k) {

        // Method : Using Kedane's Algorithm
        // Main task is to find this positive part ( this will be our max sum )

        /* 
         * Case 1 : sum of array is positive
         * Ans = (k-2)*sum + Kedanes(2 arrays joined end to end)
         * Because (k-2)*sum will be a positive part so adding it will def increase our total sum
         * And Kedanes(2 arrays joined end to end) is used to find the sum when we discard the 
         * negative part from left end of one array, and negative part from right end from another array
         */

        /*
         * Case 2 : sum of array is negative
         * Ans = Kedanes(2 arrays joined end to end)
         */

        int MOD = 1_000_000_007;

        if(k == 1) return kedanes(arr, 1);
        else{

            long sum = 0;
            for(int val:arr) sum += val;

            if(sum>=0) return (int)(((k-2)*sum)%MOD + kedanes(arr, 2)); 
            else return kedanes(arr, 2);

        }

    }

    public int kedanes(int[] nums, int ch) {

        int MOD = 1_000_000_007;
        long cmax = 0, omax = 0;
        if(ch == 1){
            for(int i=0;i<nums.length;i++){
                if(cmax<0) cmax = nums[i]; 
                else cmax += nums[i]; 
                omax = Math.max(omax, cmax); 
            }   
        }else{
            for(int i=0;i<2*nums.length;i++){
                int x = (i%nums.length);
                if(cmax<0) cmax = nums[x]; 
                else cmax += nums[x]; 
                omax = Math.max(omax, cmax); 
            }  
        }      

        return (int)(omax%MOD);
    }
}


