// Runtime: O(N)
// Space: O(1) excluding inputs
// Very simple algo.
// The best subarray of the expanded arr must be one of the following patterns:
// 1) [...S2][...]...[...][S1...] for K >= 2
// 2) [...S2][...]...[...] for K >= 2
// 3) [...maxsum...] for K==1
// where :
//  each [] denotes arr and 
//  maxsum is the max sum of a sequence within arr
//  S2 is the best sum of a sequence ending at the last index
//  S1 is the best sum of a sequence starting at 0
// So we just calculate the max sum of each pattern and viola!
class Solution {
public:
    inline static constexpr int mod = 1e9+7;

    int kConcatenationMaxSum(vector<int>& arr, int k) {
        int64_t sum1 = 0;
        int64_t sum2 = 0;
        int64_t sum1best = 0; // S1
        int64_t sum2best = 0; // S2
        int64_t cursum = 0;
        int64_t maxsum = 0;
        for (int i=0;i<arr.size();i++){
            sum1 += arr[i];
            sum2 += arr[arr.size()-1-i];
            sum1best = max(sum1best, sum1);
            sum2best = max(sum2best, sum2);
            cursum = max((int64_t)0, cursum + arr[i]);
            maxsum = max(maxsum, cursum);
        }

        int64_t ans = max(maxsum, sum2best  + (k-1)*sum1);
        if (k >= 2)
            ans = max(ans, sum1best + sum2best  + (k-2)*max((int64_t)0,sum1));
        return ans % mod;
    }
};
```

