def kConcatenationMaxSum(self, arr: List[int], k: int) -> int:
	def mss(arr):
		acc = res = 0
		for n in arr:
			acc += n
			if acc < 0:
				acc = 0
			res = max(acc, res)
		return res
	r1, r2 = mss(arr), mss(arr * 2)
	if k == 1:
		return r1
	else:
		return max(r2, sum(arr) * k - (sum(arr) - r1)) %(10**9+7)
