class Solution {
public:
    int kConcatenationMaxSum(vector<int>& arr, int k) {

        int n=arr.size();

        vector<int>pref(n),suff(n);

        pref[0]=arr[0];
        int mx1=pref[0];

        for(int i=1;i<n;i++){
            pref[i]=pref[i-1]+arr[i];
            mx1=max(mx1,pref[i]);
        }

        suff[n-1]=arr[n-1];

        int mx2=suff[n-1];

        for(int i=n-2;i>=0;i--){
            suff[i]=suff[i+1]+arr[i];
            mx2=max(mx2,suff[i]);
