        int sum=pref[n-1];

        int total1=kadans(arr);  // considering only one array

        if(k==1)
            return total1;

        int total2=mx1+mx2; // considering only 2 array


        int total3=mx1+mx2+1ll*sum*(k-2)%mod; // taking all arrays


        return max({total1,total2,total3})%mod;

