class Solution {
public:
    int kConcatenationMaxSum(vector<int>& arr, int k) {
        int sum = arr[0], mx = arr[0];
        int n = arr.size();
        int64_t total = accumulate(arr.begin(), arr.end(), 0), mod = 1e9+7;
