int kadane(int* arr, int arrSize,int degree){
    int i,max=arr[0],tmp=0;
    for(i=0;i<arrSize*degree;i++){
        tmp += arr[i%arrSize];
        if(tmp<0){
            tmp=0;
        }
        if(max<tmp){
            max=tmp;
        }
    }
    return max;
