int kConcatenationMaxSum(int* arr, int arrSize, int k){
    if(k==1){
        return kadane(arr,arrSize,1);
    }
    long long i,sum=0;
    long long N=1000000007;
    for(i=0;i<arrSize;i++){
        sum+=arr[i];
    }
    if(sum>0){
        return (kadane(arr,arrSize,2)+((k-2)*sum)%N)%N;
    }else{
        return (kadane(arr,arrSize,2))%N;
    }
}
