int kadanes(vector<int>& arr, int k){
        int mod=(int)(1e9+7);

        long csum=0, msum=0;
        int i=0;

        while(k>0){
            while(i<arr.size()){
                csum += arr[i];

                if(csum<0) csum=0;
                msum= max(csum, msum);
                i++;
            }
            i=0;
            k--;
        }

        return (int)(msum % mod);
