    int kConcatenationMaxSum(vector<int>& arr, int k) {
        int ans_for_2=0;
        long mod = (int)(1e9+7);

        long arr_sum = 0;
        for(int& e:arr)  arr_sum+=e;

        for(int i=1; i<=2; i++){
            int sum = kadanes(arr,i);

            if(k==i){
                return sum;
            }

            if(i==2){
                ans_for_2=sum;
            }
        }

        if(arr_sum < 0){
            return (int)(ans_for_2 % mod);
        }

        int ans = (int)(ans_for_2 + ((k-2) * (arr_sum) % mod));

        return max(ans,0);
    }
