class Solution {
    public int kConcatenationMaxSum(int[] arr, int k) {

        // Prefix Sum
        int n=arr.length;
        long prefix_sum=arr[0];
        long max_prefix_sum=arr[0];
        for(int x=1;x<n;x++)
        {
            prefix_sum+=arr[x];
            max_prefix_sum=Math.max(max_prefix_sum, prefix_sum);
        }

        if(max_prefix_sum <0)
        {
            prefix_sum=0;
        }
        //Kadens Algorithm
        long curr_sum=arr[0];
        long best_sum=arr[0];
        for(int x=1;x<n;x++)
        {
            curr_sum=Math.max(curr_sum+arr[x], arr[x]);
            best_sum=Math.max(curr_sum, best_sum);
