class Solution {

  private static final int MOD = (int) 1e9 + 7;

  // T = O(N), S = O(1)
  public int kConcatenationMaxSum(int[] arr, int k) {
    int n = arr.length;
    long max = getMaxSubSum(arr, n);
    if (k == 1) return asIntMod(max);
    max = Math.max(max, getMaxSubSum(arr, n * 2));
    return asIntMod(Math.max(max, getKConcatMaxSum(arr, k)));
  }

  private long getMaxSubSum(int[] arr, int n) {
    long sum = 0, max = 0;
    for (int i = 0; i < n; i++) {
      sum += arr[i % arr.length];
      max = Math.max(max, sum);
      if (sum < 0) sum = 0;
    }
    return max;
  }

  private int asIntMod(long num) {
    return (int) (num % MOD);
  }

  private long getKConcatMaxSum(int[] arr, int k) {
    int n = arr.length;
    long left = 0, right = 0, minLeft = 0, minRight = 0;
    for (int i = 0; i < n; i++) {
      left += arr[i];
      right += arr[n - 1 - i];
      minLeft = Math.min(minLeft, left);
      minRight = Math.min(minRight, right);
    }
    return (left * k) + -minLeft + -minRight;
  }
}
