class Solution(object):
	def kConcatenationMaxSum(self, arr, k):
		"""
		:type arr: List[int]
		:type k: int
		:rtype: int
		"""
		def maxSubArray(nums):
			arr = [0] * len(nums)
			arr[0] = nums[0]
			for i in range(1,len(nums)):
				if arr[i-1] < 0:
					arr[i] = nums[i]
				else:
					arr[i] = arr[i-1] + nums[i]
			return max(arr)
		if k == 0:
			return 0

		sums = sum(arr)
		newArr = arr * 2
		rst = maxSubArray(newArr)

		if rst < 0:
			return 0

		if sums > 0:
			return (sums * (k-2) + rst) % (pow(10, 9) + 7)
		else:
			return rst % (pow(10, 9) + 7)
