class Solution {
public:

    int kConcatenationMaxSum(vector<int>& A, int k) {
        int n = A.size();
        /*

        There are 4 cases to consider
        1. when sum of all element is answer => k*total
        2. when sum of all element is negative => 0
        3. when sum of array is positive and some contigous part is 
            responsible for solution => kadane +(k-2)*total
        4. when sum of array is negative and some contiguous part is
            responsible for solution => return kadane
        */
        const int mod = (int)(1e9 + 7);
        long total = 0;
        for(int i=0;i<n;i++){
            total += A[i];
            total %= mod;
        }
        long curr_sum = 0, g_sum = 0;
        for(int i=0;i<(k>1 ? 2 : 1)*n;i++){
            curr_sum += A[i%n];
            curr_sum %= mod;
            if(curr_sum < A[i%n])
                curr_sum = A[i%n];
            if(curr_sum > g_sum)
                g_sum = curr_sum;
        }
        int a = g_sum, b = (a + (k>1 ? ((k-2)*1ll*total)%mod : 0))%mod;
        return max({0, a, b});
    }
};
