            return (1LL * s * k) % mod;
        }
        int m = k;
        k = min(2, k);
        for(int j = 0; j < (n * k); ++j) {
            v[j] = arr[j % n];
        }
        long long p = 0, q = 0;
        for(int i = 0; i < k * n; ++i) {
            q = max(q + v[i] % mod, 1LL * v[i]) % mod;
            p = max(p, q) % mod;
        }

        if(s < 0) return p;
        m = m - 2;
        long long ans = p;
        while(m-- > 0) ans = (ans + s) % mod;
        return ans;
    }
};

```

