class Solution {
    int M = 1000000007;
public:
    int kadane(vector<int>&nums)
    {
        int max_sum = 0;
        int cur_sum = 0;
        for(int i:nums)
        {
            cur_sum +=i;
            if(cur_sum<0) cur_sum = 0;
            max_sum = max(cur_sum,max_sum);
        }
        return max_sum;
    }
    int kConcatenationMaxSum(vector<int>& arr, int k) {
        long long int res = 0;
        vector<int> temp(arr);
        temp.insert(temp.end(),temp.begin(),temp.end());
        // where to insert , start , end
        if(k==1) return kadane(arr) % M;
        if(k==2) return kadane(temp) % M;
        int arr_sum = 0;
        for(auto i:arr) arr_sum+=i;
        if(arr_sum>0)
        {
            res = (long long) (k-2) * arr_sum % M;
            res = (res + kadane(temp)) % M;
        }
        else res = kadane(temp) % M;
        return res;
    }
};


