class Solution {
    public int kConcatenationMaxSum(int[] arr, int k) {
        int maxForK1 = SolveFor1(arr), max = maxForK1 , pref = 0 , bestPref = 0 , suff =0, bestSuff = 0, totalSum =0;

        if (k==1) return maxForK1;

        for (int num:arr) {
            totalSum += num;
            pref += num;
            bestPref = Math.max(pref,bestPref);
        }

        for (int i=arr.length-1; i>=0; i--){
            suff += arr[i];
            bestSuff = Math.max(suff,bestSuff);
        }

        if (k>2) {
            //totalSum *= k-2;  mod 1000000007 version 
            int x = totalSum;
            for (int i=0;i<k-3; i++){
                totalSum = (totalSum + x) % 1000000007;
            }
            totalSum= Math.max (0,totalSum);
        }
        else totalSum = 0;

