        long sum=Arrays.stream(arr).sum();
        int sol=0;
        if(k==1)
            sol=(int)(kadane(arr)%mod);
        else if(sum>0)
            sol=(int)((((k-2)*sum)%mod + kadane2s(arr)%mod)%mod);
        else
            sol=(int)(kadane2s(arr)%mod);
        return Math.max(sol,0);          
