var kConcatenationMaxSum = function(arr, k) {
    const MODULO = 10 ** 9 + 7;
    const size = arr.length;
    const minK = Math.min(2, k);
    const sum = arr.reduce((total, num) => total + num);
    let currnetSum = result = 0;

    for (let index = 0; index < size * minK; index++) {
        const num = arr[index % size];

        currnetSum = Math.max(currnetSum + num, num);
        result = Math.max(result, currnetSum);
    }
    return Math.max(result, sum * Math.max(0, k - 2) + result) % MODULO;
};
